const initialState = {
  total: 0,
  history : []
};

export default function calculate(state = initialState, action) {
  switch(action.type) {
    case "ADD":
      return {
        ...state,
        total: state.total + action.value
      }
      // return state + action.value;
    case "SUBTRACT":
      return {
        ...state,
        total: state.total - action.value
      }
      // return state - action.value;
    default:
      return state;
  }
}
